import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './style.css';

class Products extends Component {
    constructor(props) {
        super(props);
        this.state = {
            property: false
        }
        this.handleDelete = this.handleDelete.bind(this);
    }

    handleDelete () {
        handleDelete = (event, id) => {
            event.preventDefault();

            // handle the deletion of a store entry in the DB
            axios.delete(`http://localhost:3001/api/inventory/${id}`, { headers: { 'Accept': 'application/json' } })
                .then(response => {
                    //remove it from store products
                    let updatedProducts = this.state.store_products.filter(prod => prod.inventory_id !== id)
                    this.setState({ store_products: updatedProducts })
                })
                .catch(e => {
                    console.log(e);
                });
        }
    // remove all entries from the products page and inventory

    }

    render() {
        let products =
            this.props.products.map(product => (
                <div key={product.id} className="prod_row">               
                    <div className="prod_col">
                        <img src={product.product_image_url} crossOrigin="true" alt={product.product_name} className="product_img prod_col" />
                    </div>
                    <div className="prod_col">{product.product_name}</div>
                    <div className="prod_col">
                        ${product.product_price.toFixed(2)}
                        {/* <input
                            type="text"
                            value={this.state.newQty}
                            onChange={this.handleQtyChange}
                            className="input_field"
                            id="qty"
                            aria-describedby="newQuantity"
                            placeholder="New price"
                        /> */}
                    </div>
                    <div className="prod_col">{product.product_comment}</div>
                    <div className="prod_col" id="action_btn_block_prod">
                        <div id="update_product"><button onClick={() => this.handleDelete(product.id)} className="btn btn-outline-danger">Delete From All Stores</button></div>
                        {/* <div id="update_product"><button onClick={this.handleUpdate} className="btn btn-outline-primary">Update</button></div> */}
                    </div>
                </div>
            ));

        return (
            <div className="wrapper">
                <h2 className="header">View All Products:</h2>
                <div id="add_btn_stores"><Link to={`/products/add`}><button className="btn btn-outline-primary">Add New Product</button></Link></div>
                <div className="prod_row_header">
                    <h3>Image</h3>
                    <h3>Name</h3>
                    <h3>Suggested Retail Price</h3>
                    <h3>Comment</h3>
                    <h3>Action</h3>
                </div>
                {products}
            </div>
        )
    }
}

export default Products;
