import React, { Component } from 'react';
import swal from 'sweetalert';
import axios from 'axios';
import './style.css';

class StoreAdd extends Component {
    constructor(props) {
        super(props);
        this.state = {
            store_name: 'test',
            store_city: 'test',
            store_state: 'CA'
        }
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSelectChange = this.handleSelectChange.bind(this);
    }

    // getComments = async () => {

    //     const { id } = this.props.match.params;
    //     try {
    //         const commentResponse = await axios.get(`/api/stores/comments/${id}`);
    //         const comments = commentResponse.data;
    //         const { blog } = comments[0]
    //         console.log(blog);
    //         console.log('I am comments', comments);
    //         this.setState({ blog, comments });
    //     } catch (e) {
    //         console.log(e);
    //     }
    // }

    // getProducts = async () => {

    //     const { id } = this.props.match.params;
    //     try {
    //         const products_res = await axios.get(`/api/stores/${id}`);
    //         console.log(products_res)
    //         const store_name = products_res.data[0].store_name;
    //         const products_list = products_res.data;
    //         this.setState({ store_name: store_name, storeProducts: products_list });
    //         console.log(this.state)
    //     } catch (e) {
    //         console.log(e);
    //     }
    // }

    // handleInputChange = event => {
    //     const { value } = event.target;
    //     this.setState({ userCommentField: value });
    // }

    handleInputChange(e) {
        let value = e.target.value;
        let name = e.target.name;
        this.setState(prevState => ({
            ...prevState, [name]: value

        }), () => console.log(this.state))
    }

    handleSelectChange(e) {
        let value = e.target.value;
        let name = e.target.name;
        this.setState(prevState => ({
            ...prevState, [name]: value

        }), () => console.log(this.state))
    }

    handleSubmit = async event => {


        event.preventDefault();
        // const { userCommentField: comment } = this.state.store_name;
        // try {
        //     const response = await axios.post(`/api/blogs/${this.props.match.params.id}`, { comment });
        //     this.getComments();
        // } catch (e) {
        //     console.log(e);
        // }
        let storeName = this.state.store_name;
        let storeCity= this.state.store_city;
        let storeState = this.state.store_state;

        console.log(storeName + " at " +storeCity+", "+storeState )
        // create a multipart formData object to encode the info in the post
        const formData = new FormData();
        const config = {
            headers: {
                'content-type': 'multipart/form-data'
            }
        }

        // add user data to the form object
        formData.append('store_name', storeName);
        formData.append('store_city', storeCity);
        formData.append('store_state', storeState);

        console.log(...formData);

        // handle the submission of the form data to the database
        axios.post('http://localhost:3001/api/stores', form,  {headers: {'Accept': 'application/json'}})
            .then(response => {
                if (response.data.status === true) {
                    swal("Congrats!", "Your store has been added!", "info")
                        .then((value) => {
                            window.location.href = "/stores"
                        })
                }
                if (response.data.status === false || response.data.status === '500') {
                    swal("Oops!", "Something went wrong adding this store.", "error");
                }
            });
    }

    render() {
        return (
            <div className="wrapper">
                <form className="container-fluid" encType="multipart/form-data">
                    <h1 className="header">Add a Store</h1>
                    <div className="add_row">
                        <label>Store Name: </label>
                        <input
                            type="text"
                            id="store_name"
                            className="form-control"
                            name={"store_name"}
                            onChange={(e) => this.handleInputChange(e)}
                            value={this.state.store_name}
                            required
                        />
                    </div>
                    <div className="add_row">
                        <label>Store City:</label>
                        <input
                            type="text"
                            id="store_city"
                            className="form-control"
                            name={"store_city"}
                            onChange={(e) => this.handleInputChange(e)}
                            value={this.state.store_city}
                            required
                        />
                    </div>
                    <div className="add_row">
                        <label>Store State:</label>
                        <select 
                        onChange={(e) => this.handleSelectChange(e)} 
                        name="store_state"
                        className="form-control"
                        id={this.state.store_state}>
                            <option value="">-- Select --</option>
                            <option value="AL">Alabama</option>
                            <option value="AK">Alaska</option>
                            <option value="AZ">Arizona</option>
                            <option value="AR">Arkansas</option>
                            <option value="CA">California</option>
                            <option value="CO">Colorado</option>
                            <option value="CT">Connecticut</option>
                            <option value="DE">Delaware</option>
                            <option value="DC">District Of Columbia</option>
                            <option value="FL">Florida</option>
                            <option value="GA">Georgia</option>
                            <option value="HI">Hawaii</option>
                            <option value="ID">Idaho</option>
                            <option value="IL">Illinois</option>
                            <option value="IN">Indiana</option>
                            <option value="IA">Iowa</option>
                            <option value="KS">Kansas</option>
                            <option value="KY">Kentucky</option>
                            <option value="LA">Louisiana</option>
                            <option value="ME">Maine</option>
                            <option value="MD">Maryland</option>
                            <option value="MA">Massachusetts</option>
                            <option value="MI">Michigan</option>
                            <option value="MN">Minnesota</option>
                            <option value="MS">Mississippi</option>
                            <option value="MO">Missouri</option>
                            <option value="MT">Montana</option>
                            <option value="NE">Nebraska</option>
                            <option value="NV">Nevada</option>
                            <option value="NH">New Hampshire</option>
                            <option value="NJ">New Jersey</option>
                            <option value="NM">New Mexico</option>
                            <option value="NY">New York</option>
                            <option value="NC">North Carolina</option>
                            <option value="ND">North Dakota</option>
                            <option value="OH">Ohio</option>
                            <option value="OK">Oklahoma</option>
                            <option value="OR">Oregon</option>
                            <option value="PA">Pennsylvania</option>
                            <option value="RI">Rhode Island</option>
                            <option value="SC">South Carolina</option>
                            <option value="SD">South Dakota</option>
                            <option value="TN">Tennessee</option>
                            <option value="TX">Texas</option>
                            <option value="UT">Utah</option>
                            <option value="VT">Vermont</option>
                            <option value="VA">Virginia</option>
                            <option value="WA">Washington</option>
                            <option value="WV">West Virginia</option>
                            <option value="WI">Wisconsin</option>
                            <option value="WY">Wyoming</option>
                        </select>
                    </div>
                    <div className="add_store_action_btn_block">
                        <button onClick={this.handleSubmit} className="btn btn-outline-primary action_btn">Submit</button>
                        <button onClick={this.handleCancel} className="btn btn-outline-danger action_btn">Cancel</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default StoreAdd;
