import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './style.css';

class Stores extends Component {
    constructor(props) {
        super(props);
        this.state = {
            userLoggedIn: false
        }
        this.handleDeleteStore = this.handleDeleteStore.bind(this);
    }
    handleDeleteStore = () => {
        event.preventDefault();

        let storeName = this.state.store_name;
        let storeCity = this.state.store_city;
        let storeState = this.state.store_state;

        if (storeName === '') {
            swal("Oops!", "Store Name is a required field.", "error");
        } else if (storeCity === '') {
            swal("Oops!", "Store City is a required field.", "error");
        } else if (storeState === '') {
            swal("Oops!", "Store State is a required field.", "error");
        }

        if (storeName !== '' && storeCity !== '' && storeState !== '') {
            // handle the submission of the form data to the database
            axios.post('http://localhost:3001/api/stores', { store_name: storeName, store_city: storeCity, store_state: storeState }, { headers: { 'Accept': 'application/json' } })
                .then(response => {
                    console.log(response)
                    if (response.status === 200) {
                        swal("Success!", "A new store has been added!", "info")
                            .then((value) => {
                                window.location.href = "/stores"
                            })
                    }
                    if (response.data.status === false || response.data.status === '500') {
                        swal("Oops!", "Something went wrong adding this store.", "error");
                    }
                });
        }
    }

    render() {
        let stores = this.props.stores.map(store => (
            <div key={store.id} className="stores_row">
                <div className="stores_col">{store.store_name}</div>
                <div className="stores_col">{store.store_city}, {store.store_state}</div>

                <div className="stores_col" id="action_btn_block_stores">
                    <Link to={`/stores/${store.id}`}><button className="btn btn-outline-primary action_btn_stores">View Store</button></Link>
                    {/* <Link to={`/stores/delete/${store.id}`}><button className="btn btn-outline-danger action_btn_stores">Delete Store</button></Link> */}
                   <button onClick={this.handleDeleteStore} className="btn btn-outline-danger action_btn_stores">Delete Store</button>

                </div>
            </div>
        ));

        return (
            <div className="wrapper">
                <h2 className="header">Stores:</h2>
                <div className="stores_row_header">
                    <h3>Name</h3>
                    <h3>Location</h3>
                    <h3>Actions</h3>
                </div>
                {stores}
                <Link to={`/stores/add`}><button className="btn btn-outline-primary action_btn_stores">Add Store</button></Link>
                {/* <div id="add_store"><button onClick={this.handleAddStore} className="btn btn-outline-primary">Add Store</button></div> */}
            </div>
        )
    }
}

export default Stores;
